           #{title}, #{category}, #{abstractText}, #{contents}, #{attachmentLink}, #{coverImage}, #{creationTime}, #{teamID}, #{status}, #{viewStatus}
       )

mapper.update:
UPDATE Achievement
SET status = #{status}
WHERE achievementID = #{achievementID}

UPDATE Achievement
SET viewStatus = #{viewStatus}
