mapper.insert:
INSERT INTO TeamAdministrator (
    adminID, publishAchievement, userPermission, deleteAchievement, teamID
)
VALUES (
           #{adminID}, 1, 1, 0, 1
       )

mapper.update:
UPDATE [TeamAdministrator]
SET publishAchievement=1, userPermission=1
WHERE adminID=#{adminID}

mapper.select:
SELECT userPermission
FROM TeamAdministrator
WHERE adminID = #{adminID}
